import React, { useRef } from "react";
import emailjs from "emailjs-com";
import { Container } from "react-bootstrap";
import "./styles/reachMePageStyle.css";

export default function ReachMePage() {
  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm(
        "gmail",
        "template_fshjiyd",
        e.target,
        "user_uBuqnrXsIfeBtcbNRmIeK"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );

    e.target.reset();

    return (
      <div className="ReachMePage" id="reach-me-page">
        <Container>
          <h1 className="ReachMePage-header">Reach Me!</h1>
          <div class="form-group pink-border-focus">
            <form onSubmit={sendEmail}>
              <textarea
                class="form-control"
                id="exampleFormControlTextarea5"
                rows="5"
                placeholder="Leave your message here!"
                name="message"
              ></textarea>
              <button type="submit">Send</button>
            </form>
          </div>
        </Container>
      </div>
    );
  }
}
