import React, { useState, useRef } from "react";
// import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Card, Accordion, Button } from "react-bootstrap";

const MainContent = () => {
  // would basically hold the active eventKey to control the active/expanded Accordion
  const [activeKey, setActiveKey] = useState("");

  // we are going to need a ref to the Accordion element to get its position/use the scrollIntoView function
  const accordElem = useRef(null);

  const handleClickEdit = () => {
    setActiveKey("0"); // "0" here is as defined in your Accordion.Collapse eventKey
    accordElem.current.scrollIntoView({
      behavior: "smooth",
      block: "end",
      inline: "nearest",
    }); // initiate scroll to the "AddMock" Accordion component
  };

  return (
    <div>
      <AddMock
        activeKey={activeKey}
        setActiveKey={setActiveKey}
        ref={accordElem}
      />
    </div>
  );
};

// here, ref forwarding is needed to assign a ref to a `div` DOM element
const AddMock = React.forwardRef((props, ref) => {
  // optional re-toggling of expanded accordion
  function handleClickToggle(eventKey) {
    if (eventKey === props.activeKey) {
      props.setActiveKey("");
    } else {
      props.setActiveKey(eventKey);
    }
  }

  return (
    <div className="row" ref={ref}>
      <Accordion style={{ paddingLeft: "32px" }} activeKey={props.activeKey}>
        <Card className="collapseStyle">
          <Card.Header>
            <Accordion.Toggle
              as={Button}
              variant="link"
              eventKey="0"
              onClick={() => handleClickToggle("0")}
            >
              Add Mock
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body>
              <Container className="mockbody">
                Some Content. Lorem Ipsum. Hello World.
              </Container>
            </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    </div>
  );
});

export default MainContent;
